<?php!isset($_REQUEST['a']) ? $_action = '' : $_action = $_REQUEST['a'];switch($_action) {	default:        if (isset($_POST['mail']) && isset($_POST['message'])) {            if (!empty($_POST['mail']) && !empty($_POST['message'])) {                if (filter_var($_POST['mail'], FILTER_VALIDATE_EMAIL)) {                    $message = htmlentities(addslashes(nl2br($_POST['message'])));                    $DB->contactCreate($_POST['mail'], $message);                    add_message('success', 'Le formulaire a été envoyé');                } else {                    add_message('danger', 'Le format du mail est incorrect');                }            } else {                add_message('danger', 'Un des champs est vide');            }        }        include URI_VUE.'/contact/v_index.php';        break;}