<?php!isset($_REQUEST['a']) ? $_action = '' : $_action = $_REQUEST['a'];switch($_action) {	default:        if (isset($_POST['nomFamille']) && isset($_POST['adresseFamille'])  && isset($_POST['cpFamille']) && isset($_POST['telFamille']) && isset($_POST['mailFamille']) && isset($_POST['villeFamille'])) {            if (!empty($_POST['nomFamille']) && !empty($_POST['adresseFamille']) && !empty($_POST['cpFamille']) && !empty($_POST['telFamille']) && !empty($_POST['mailFamille']) && !empty($_POST['villeFamille'])) {                $DB->ajoutFamille($_POST['nomFamille'], $_POST['adresseFamille'], $_POST['cpFamille'], $_POST['telFamille'], $_POST['mailFamille'], $_POST['villeFamille']);                add_message('success', 'Le formulaire a été envoyé');            } else {                add_message('danger', 'Un des champs est vide');            }        }        include URI_VUE . '/famille/v_famille.php';    break;}