<?phpfunction crypt_Password( $pwd ) {	return sha1('7@4^{71:6[65%1~)' . $pwd . '>2@%4$995-3>4[_2');}function userIsConnected() {    return isset($_SESSION['user']) && !empty($_SESSION['user']);}function reduceStringLenght($message){    if (strlen($message) > 20) {//        return substr($message,0,5) . ' [ message long ]';        return '<span style="font-weight: bold;">Message trop long ...</span>';    } else {        return $message;    }}