<?php//ajouter une erreur ! !function add_message($type , $error, $notification = false){    // on ajoute l'erreur !    $_SESSION['errors'][$type][] = [        'message' => $error,        'notification' => $notification,    ];}//afficher les erreurs !function display_message($notification = false){    $html = "";    // on affiche les erreurs !    if(!empty($_SESSION['errors'])){        foreach($_SESSION['errors'] as $type => $a_data){            foreach($a_data as $key => $message_data){                if ($message_data['notification'] == $notification) {                    $html .= '<div class="alert alert-'.$type.' '. ($notification ? 'notification' : '').'" >';                    $html .= $message_data['message'];                    $html .= $notification ? '<button class="close">+</button>' : '';                    $html .= '</div>';                    // on supprime les erreurs !                    unset($_SESSION['errors'][$type][$key]);                    if (count($_SESSION['errors'][$type]) == 0) {                        unset($_SESSION['errors'][$type]);                        if (count($_SESSION['errors']) == 0) {                            unset($_SESSION['errors']);                        }                    }                }            }        }    }    //retourne html    return $html;}