<?php// Source : http://css-tricks.com/snippets/php/sanitize-database-inputs/// Function for stripping out malicious bits    function clean_input($input) {        $search = array(        '@<script[^>]*?>.*?</script>@si', // Strip out javascript        '@<[\/\!]*?[^<>]*?>@si', // Strip out HTML tags        '@<style[^>]*?>.*?</style>@siU', // Strip style tags properly        '@<![\s\S]*?--[ \t\n\r]*>@' // Strip multi-line comments        );        $output = preg_replace($search, '', $input);        return $output;    }/* escape strings for SQL (replace ' and ") */    function clean_quote($string) {        $string = preg_replace('#\'#', '&acute;', $string);        $string = preg_replace('#"#', '&quot;', $string);        $string = preg_replace('#<#', '&lt;', $string);        $string = preg_replace('#>#', '&gt;', $string);       $string = htmlspecialchars( $string ,ENT_QUOTES);       return $string;    }// Sanitization function    function clean($input) {        if (is_array($input)) {            foreach($input as $var=>$val) {                $output[$var] = clean($val);            }        }        else {            if (get_magic_quotes_gpc()) {                $input = stripslashes($input);            }            $input = clean_input($input);            $output = clean_quote($input);//	        $output = htmlentities($input);        }        return $output;    }	function clear($input){		if (is_array($input)) {			foreach($input as $var=>$val) {				$output[$var] = clean($val);			}		}		else {			if (get_magic_quotes_gpc()) {				$input = stripslashes($input);			}			$output = html_entity_decode($input);		}		return $output;	}?>